import React, {useState} from "react";
import { NavLink } from "react-router-dom";

const Footer = () => {

  const [Mail, setEmail] = useState({
    email : ""
  });

  const [errors, setErrors] = useState({
    email: ''
  });

  // Handle Inputs
  const handleChange = (event) =>{
    let name = event.target.name;
    let value = event.target.value;

    setEmail({...Mail, [name]:value});
  }

  // Handle Submit
  const handleSubmit = async (event)=>{
    event.preventDefault();
    // Object DeStructuring
    // Store Object Data into Variables
    const {email} = Mail;

    // Validation
    let hasErrors = false;
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

    // Validation for empty fields
    if (!email.trim()) {
      setErrors({
        email: email.trim() ? '' : 'Email is required',
      });
      return; // Don't proceed with form submission if any field is empty
    }

    if (!emailPattern.test(email)) {
      setErrors({ ...errors, email: 'Invalid email format' });
      hasErrors = true;
    }
    if (hasErrors) {
      return; // Don't proceed with form submission if there are validation errors
    }
    try {

      const res = await fetch('http://localhost:3001/Subscription', {
        method : "POST",
        headers : {
          "Content-Type" : "application/json"
        },
        body : JSON.stringify({email})
      })
      console.log(res.status)
      if(res.status === 400 || !res){
        window.alert("Message Not Sent. Try Again Later")
      }else{
        // You need to Restart the Server for Proxy Works
        // Now Try Again
        window.alert("you have been subscribed");
        setEmail({
          email : ""
        })
        
      }
    } catch (error) {
      console.log(error);
    }
  }

  return (
    <footer className="footer text-white bg-primary py-2">
      <div className="container">
        <div className="row">
          <div className="col-md-3 col-sm-6">
            <h4>POLY ASSISTANT</h4>
          </div>
          <div className="col-md-3 col-sm-6">
            <h5>Navigation</h5>
            <ul className="nav flex-column">
              <li className="nav-item mb-2">
                <NavLink to="/" className="nav-link p-0 text-white">
                  Home
                </NavLink>
              </li>
              <li className="nav-item mb-2">
                <NavLink to="/About" className="nav-link p-0 text-white">
                  About
                </NavLink>
              </li>
              <li className="nav-item mb-2">
                <NavLink to="/Contact" className="nav-link p-0 text-white">
                  Contact Us
                </NavLink>
              </li>
            </ul>
          </div>
          <div className="col-md-6 col-sm-12">
            <h5>Subscribe to our newsletter</h5>
            <p>Monthly digest of what's new and exciting from us.</p>
            {/* <form onSubmit={handleSubmit}> */}
            <div className="input-group">
              <input
                type="email"
                className="form-control"
                placeholder="Email address"
                name="email"
                value={Mail.email}
                onChange={handleChange}
              />
              {errors.email && <p className="text-danger">{errors.email}</p>}
              <button className="btn btn-dark rounded-end" type="button" onSubmit={handleSubmit}>
                Subscribe
              </button>
            </div>
            {/* </form> */}
          </div>
        </div>
        <div className="row mt-2 border-top">
          <div className="col-md-6 col-sm-12">
            <p>© 2024 Polytech Paris Saclay, Inc. All rights reserved.</p>
          </div>
          <div className="col-md-6 col-sm-12 text-end">
            <ul className="list-inline">
              <li className="list-inline-item me-3">
                <NavLink className="link-light" to="#">
                  <i className="fa fa-facebook fa-lg"></i>
                </NavLink>
              </li>
              <li className="list-inline-item me-3">
                <NavLink className="link-light" to="#">
                  <i className="fa fa-instagram fa-lg"></i>
                </NavLink>
              </li>
              <li className="list-inline-item">
                <NavLink className="link-light" to="#">
                  <i className="fa fa-twitter fa-lg"></i>
                </NavLink>
              </li>
            </ul>
          </div>
        </div>
      </div>
    </footer>
  );
};

export default Footer;






